package textadventure;import items.Container;/** * A Player can contain objects and knows the Room it's currently in. */public class Player extends Container {	/** Room this Player is in at the moment */	private Room currentRoom;		private int health;		private boolean hasBrushedTeeth = false;		private boolean isWearingClothes = false;		/** Creates a player that starts in the given Room with no items */	public Player(Room startRoom, World world, String name, String description) {		super(world, name, description);		currentRoom = startRoom;		health = 0;	}	/* Getters and setters */	public Room getCurrentRoom() {		return currentRoom;	}	public void setCurrentRoom(Room newRoom) {		currentRoom = newRoom;	}		public void setHealth(int newHealth) {		health = newHealth;	}		public int getHealth() {		return health;	}		public void setBrush(boolean brushed) {		hasBrushedTeeth = brushed;	}		public boolean getBrush() {		return hasBrushedTeeth;	}		public void clothe(boolean clothe) {		isWearingClothes = clothe;	}		public boolean isClothed() {		return isWearingClothes;	}}